// MARK:- UItableView Delegate & DataSource
extension ViewController: UITableViewDelegate, UITableViewDataSource {
    
    func tableView(tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
       return arr.count
    }
    
    func tableView(tableView: UITableView, cellForRowAtIndexPath indexPath: NSIndexPath) -> UITableViewCell {
        var cell = tableView.dequeueReusableCellWithIdentifier("cellIdentifier")
        
        if cell == nil {
            cell = UITableViewCell(style: .Default, reuseIdentifier: "cellIdentifier")
        }
        
        cell?.textLabel?.text = arr[indexPath.row]
        cell?.textLabel?.numberOfLines = 0
        
        return cell!
    }
    
    func tableView(tableView: UITableView, didSelectRowAtIndexPath indexPath: NSIndexPath) {
        
        selectedCell = tableView.cellForRowAtIndexPath(indexPath)
        selectedCellRow = indexPath.row
        tableView.reloadData()
        
    }
    
    func tableView(tableView: UITableView, heightForRowAtIndexPath indexPath: NSIndexPath) -> CGFloat {
        
        if selectedCell != nil && selectedCellRow == indexPath.row {
            
            let str: NSString = arr[selectedCellRow]
            let lableSize = CGSizeMake((selectedCell?.textLabel?.bounds.size.width)!, CGFloat.max)
            
            let textRectt = str.boundingRectWithSize(lableSize, options: .UsesLineFragmentOrigin, attributes: [NSFontAttributeName: (selectedCell?.textLabel?.font)!], context: nil)
            
            return textRectt.size.height + 10
        } else {
            return 44
        }
        
    }
}




























